(247, 24, 1, '', '2014-02-03 13:27:30', 899, 2019, 'c0e234f305bff0c9023c521bf83dac8167ab3793', '{"id":24,"asset_id":"126","title":"Mithuna","alias":"gemini","introtext":"<p>&nbsp; &nbsp; &nbsp;Gemini is known as Mithuna in Vedic Astrology. This windy sign is male by gender and rules the west direction. It gives Vatta Dosha. It rules the chest, hands and rib cage. <a href=\\"index.php?option=com_content&amp;view=article&amp;id=4:buddh&amp;catid=9:9-planets&amp;Itemid=109\\">Budh<\\/a> is the lord of Mithuna.<\\/p>","fulltext":"","state":1,"catid":"12","created":"2014-02-01 12:25:34","created_by":"899","created_by_alias":"","modified":"2014-02-03 13:27:30","modified_by":"899","checked_out":"899","checked_out_time":"2014-02-03 13:27:24","publish_up":"2014-02-01 12:25:34","publish_down":"0000-00-00 00:00:00","images":"{\\"image_intro\\":\\"\\",\\"float_intro\\":\\"\\",\\"image_intro_alt\\":\\"\\",\\"image_intro_caption\\":\\"\\",\\"image_fulltext\\":\\"\\",\\"float_fulltext\\":\\"\\",\\"image_fulltext_alt\\":\\"\\",\\"image_fulltext_caption\\":\\"\\"}","urls":"{\\"urla\\":false,\\"urlatext\\":\\"\\",\\"targeta\\":\\"\\",\\"urlb\\":false,\\"urlbtext\\":\\"\\",\\"targetb\\":\\"\\",\\"urlc\\":false,\\"urlctext\\":\\"\\",\\"targetc\\":\\"\\"}","attribs":"{\\"show_title\\":\\"\\",\\"link_titles\\":\\"\\",\\"show_tags\\":\\"\\",\\"show_intro\\":\\"\\",\\"info_block_position\\":\\"\\",\\"show_category\\":\\"\\",\\"link_category\\":\\"\\",\\"show_parent_category\\":\\"\\",\\"link_parent_category\\":\\"\\",\\"show_author\\":\\"\\",\\"link_author\\":\\"\\",\\"show_create_date\\":\\"\\",\\"show_modify_date\\":\\"\\",\\"show_publish_date\\":\\"\\",\\"show_item_navigation\\":\\"\\",\\"show_icons\\":\\"\\",\\"show_print_icon\\":\\"\\",\\"show_email_icon\\":\\"\\",\\"show_vote\\":\\"\\",\\"show_hits\\":\\"\\",\\"show_noauth\\":\\"\\",\\"urls_position\\":\\"\\",\\"alternative_readmore\\":\\"\\",\\"article_layout\\":\\"\\",\\"show_publishing_options\\":\\"\\",\\"show_article_options\\":